<?php

namespace Myexp\Bundle\CmsBundle\Repository;

use Doctrine\ORM\EntityRepository;
use Symfony\Component\Routing\Route;
use Symfony\Component\Routing\RouteCollection;

/**
 * ProductRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProductRepository extends EntityRepository implements ContentModel {

    /**
     * 获得默认路由
     * 
     * @return RouteCollection
     */
    public function getDefaultRoute($urlSurffix) {

        $routes = new RouteCollection();

        //产品列表路由
        $listPath = '/product/list/{id}' . $urlSurffix;
        $listDefaults = array(
            '_controller' => 'MyexpCmsBundle:Download:list'
        );
        $listRequirements = array(
            'id' => '\d+',
        );
        $listwRoute = new Route($listPath, $listDefaults, $listRequirements);
        $routes->add('download_list', $listwRoute);

        //文章查看路由
        $showPath = '/download/show/{id}' . $urlSurffix;
        $showDefaults = array(
            '_controller' => 'MyexpCmsBundle:Download:show'
        );
        $showRequirements = array(
            'id' => '\d+',
        );
        $showRoute = new Route($showPath, $showDefaults, $showRequirements);
        $routes->add('download_show', $showRoute);

        return $routes;
    }

    /**
     * 获得分页查询
     * 
     * @param type $params
     * @return type
     */
    public function getPaginationQuery($params = null) {
        $qb = $this->buildQuery($params);

        return $qb->getQuery();
    }

    /**
     * 构造查询
     * 
     * @param type $params
     * @return type
     */
    private function buildQuery($params) {

        $qb = $this->createQueryBuilder('p');

        if (isset($params['category'])) {
            $qb
                    ->andWhere('p.category = ?1')
                    ->setParameter(1, $params['category'])
            ;
        }
        if (isset($params['isActive'])) {
            $qb
                    ->andWhere('p.isActive = ?2')
                    ->setParameter(2, $params['isActive'])
            ;
        }

        return $qb;
    }

}
